export const i18n = new I18n(translations);

i18n.defaultLocale = "en";

// Set the locale based on device's language, with a fallback to defaultLocale
console.log("Initializing i18n...");
const deviceLanguageCode = Localization.getLocales()[0].languageCode ?? i18n.defaultLocale;
console.log("Device Language Code:", deviceLanguageCode);
i18n.locale = translations[deviceLanguageCode as keyof typeof translations]
  ? deviceLanguageCode
  : i18n.defaultLocale;
console.log("i18n.locale after initialization:", i18n.locale);

i18n.enableFallback = true;

let languageChangeCallback: ((locale: string) => void) | null = null;

export const registerLanguageChangeCallback = (callback: (locale: string) => void) => {
  languageChangeCallback = callback;
};

export const setLanguage = (locale: string) => {
  console.log("Setting i18n locale to:", locale); // Log
  i18n.locale = locale;
  if (languageChangeCallback) {
    console.log("Triggering language change callback for locale:", locale); // Log
    languageChangeCallback(locale);
  }
};